mapper.update:
UPDATE tbl_seokmember
SET USERID = #{USERID}
, PASSWORD = #{PASSWORD}
, NAME = #{NAME}
, PHONE = #{PHONE}
, ADR = #{ADR}
, ADRD = #{ADRD}
, EMAIL = #{EMAIL}
WHERE USERID = #{USERID}

mapper.select:
SELECT CONBOARD_NO, MIN(ORDER_KEY) AS ORDER_KEY, MAX(CONBOARD_TITLE) AS CONBOARD_TITLE, MAX(CONBOARD_CONTENT) AS CONBOARD_CONTENT, MAX(CONBOARD_HIT) AS CONBOARD_HIT, MAX(CONBOARD_WRITEDATE) AS CONBOARD_WRITEDATE, MAX(USERID) AS USERID, MAX(REP_CNT) AS REP_CNT, MAX(NAME) AS NAME FROM ( (SELECT 1 as ORDER_KEY, a.CONBOARD_NO, a.CONBOARD_TITLE, a.CONBOARD_CONTENT, a.CONBOARD_HIT, a.CONBOARD_WRITEDATE, a.USERID, a.NAME, IFNULL((SELECT COUNT(*) as REP_CNT FROM tbl_seokadmcontrep where CONBOARD_NO = a.CONBOARD_NO),0) AS REP_CNT FROM tbl_seokcontactboard a WHERE USERID = 'Admin' LIMIT 3) UNION (SELECT 2 as ORDER_KEY, b.CONBOARD_NO, b.CONBOARD_TITLE, b.CONBOARD_CONTENT, b.CONBOARD_HIT, b.CONBOARD_WRITEDATE, b.USERID, b.NAME, IFNULL((SELECT COUNT(*) as REP_CNT FROM tbl_seokadmcontrep where CONBOARD_NO = b.CONBOARD_NO),0) AS REP_CNT FROM tbl_seokcontactboard b
WHERE b.CONBOARD_TITLE like concat('%',#{CONBOARD_TITLE},'%')
) ) AA GROUP BY CONBOARD_NO ORDER BY ORDER_KEY,CONBOARD_NO desc

SELECT COMBOARD_NO, MIN(ORDER_KEY) AS ORDER_KEY, MAX(COMBOARD_TITLE) AS COMBOARD_TITLE, MAX(COMBOARD_CONTENT) AS COMBOARD_CONTENT, MAX(COMBOARD_HIT) AS COMBOARD_HIT, MAX(COMBOARD_WRITEDATE) AS COMBOARD_WRITEDATE, MAX(USERID) AS USERID, MAX(REP_CNT) AS REP_CNT, MAX(NAME) AS NAME FROM ( (SELECT 1 as ORDER_KEY, a.COMBOARD_NO, a.COMBOARD_TITLE, a.COMBOARD_CONTENT, a.COMBOARD_HIT, a.COMBOARD_WRITEDATE, a.USERID, a.NAME, IFNULL((SELECT COUNT(*) as REP_CNT FROM tbl_seokcomurep where COMBOARD_NO = a.COMBOARD_NO),0) AS REP_CNT FROM tbl_seokcommuboard a WHERE USERID = 'Admin' LIMIT 3) UNION (SELECT 2 as ORDER_KEY, b.COMBOARD_NO, b.COMBOARD_TITLE, b.COMBOARD_CONTENT, b.COMBOARD_HIT, b.COMBOARD_WRITEDATE, b.USERID, b.NAME, IFNULL((SELECT COUNT(*) as REP_CNT FROM tbl_seokcomurep where COMBOARD_NO = b.COMBOARD_NO),0) AS REP_CNT FROM tbl_seokcommuboard b
WHERE b.COMBOARD_TITLE like concat('%',#{COMBOARD_TITLE},'%')
) ) AA GROUP BY COMBOARD_NO ORDER BY ORDER_KEY,COMBOARD_NO DESC

mapper.update:
UPDATE
tbl_seokcontactboard
SET CONBOARD_TITLE = #{CONBOARD_TITLE}
,
CONBOARD_CONTENT = #{CONBOARD_CONTENT}
, CONBOARD_HIT = #{CONBOARD_HIT}
WHERE CONBOARD_NO = #{CONBOARD_NO}

UPDATE
tbl_seokcommuboard
SET COMBOARD_TITLE = #{COMBOARD_TITLE},
COMBOARD_CONTENT = #{COMBOARD_CONTENT}, 
COMBOARD_HIT = #{COMBOARD_HIT}
WHERE COMBOARD_NO = #{COMBOARD_NO}

UPDATE tbl_file
SET FILE_ID = #{FILE_ID}
, FILE_NAME = #{FILE_NAME}
, FILE_TYPE = #{FILE_TYPE}
, FILE_SIZE = #{FILE_SIZE}
WHERE COMBOARD_NO = #{COMBOARD_NO}

UPDATE tbl_seokcommuboard
   SET COMBOARD_HIT = ifnull(COMBOARD_HIT,0) + 1
   WHERE COMBOARD_NO = #{COMBOARD_NO}

UPDATE tbl_seokcontactboard
   SET CONBOARD_HIT = ifnull(CONBOARD_HIT,0) + 1
   WHERE CONBOARD_NO = #{paramHit}

UPDATE tbl_room SET
ROOM_NO = #{ROOM_NO}
,ROOM_TITLE = #{ROOM_TITLE}
,ROOM_INFO = #{ROOM_INFO}
,ROOM_PHOTO = #{ROOM_PHOTO}
WHERE ROOM_NO = #{ROOM_NO}

UPDATE
tbl_roomreserve
SET USERID = #{USERID}
, ROOM_NO = #{ROOM_NO}
, RESERVE_STATUS = #{RESERVE_STATUS}
, RESERVE_DATE = #{RESERVE_DATE}
, ROOM_TITLE = #{ROOM_TITLE}
WHERE RESERVE_NO = #{RESERVE_NO}

mapper.select:
SELECT a.RESERVE_NO, a.USERID, b.NAME, a.ROOM_NO, c.ROOM_INFO, c.ROOM_TITLE, a.RESERVE_STATUS, DATE_FORMAT(a.RESERVE_DATE, '%Y%m%d') AS RESERVE_DATE, CASE a.RESERVE_STATUS when '001' then 'cell_style1' when '003' then 'cell_style3' when '004' then 'cell_style4' end as BKCOL FROM tbl_roomreserve a, tbl_seokmember b, tbl_room c WHERE a.USERID = b.USERID AND a.ROOM_NO = c.ROOM_NO
AND b.NAME = #{rName}
AND a.RESERVE_STATUS = #{rStatus}
ORDER BY RESERVE_NO DESC

SELECT COUNT(*) AS CNT FROM tbl_roomreserve 
	WHERE RESERVE_DATE = #{param1} 
		AND ROOM_TITLE = #{param2}
		AND RESERVE_STATUS in (#{param3},"003")

